# Importieren des System.DirectoryServices.AccountManagement-Namespace
Add-Type -AssemblyName System.DirectoryServices.AccountManagement

# Erstellen des Kontexts für den Zugriff auf das AD
$context = New-Object -TypeName System.DirectoryServices.AccountManagement.PrincipalContext([System.DirectoryServices.AccountManagement.ContextType]::Domain)

# Abrufen der Benutzerinformationen aus dem Active Directory
$user = [System.DirectoryServices.AccountManagement.UserPrincipal]::FindByIdentity($context, $env:USERNAME)

# Ausgabe der Benutzerinformationen
Write-Host "Name: $($user.DisplayName)"
Write-Host "Titel: $($user.Title)"
Write-Host "Abteilung: $($user.Department)"
Write-Host "E-Mail: $($user.EmailAddress)"
Write-Host "Vorname: $($user.GivenName)"
Write-Host "Nachname: $($user.Surname)"
Write-Host "Telefon: $($user.VoiceTelephoneNumber)"
## Infos unter https://learn.microsoft.com/de-de/dotnet/api/system.directoryservices.accountmanagement.userprincipal?view=dotnet-plat-ext-7.0

# Base64 Bilder
$logo = "data:image/jpeg;base64,"

# Erstellen der Signaturvorlagen
$htmlTemplate = @"
<!DOCTYPE HTML PUBLIC \"-//W3C//DTD HTML 4.0 Transitional//EN\">
<html>
<head>
</head>
<body>
<div style=\"font-family: Calibri; font-size: 11pt; color: DimGray\">
<strong>Mit freundlichen Grüßen</strong><br><br>
<strong>$($user.Surname) $($user.GivenName)</strong><br>
</div>
<div style=\"font-family: Calibri; font-size: 10pt; color: DimGray\">
$($user.Title)<br>
</div>
<div style=\"font-family: Calibri; font-size: 10pt; color: SteelBlue\">
<strong>@COMPANY</strong>
</div>
<img src="$logo">
</div>
<div style=\"font-family: Calibri; font-size: 10pt; color: DimGray\">
@STREETADDRESS, @POSTALCODE @CITY<br>
<strong>TEL</strong> $($user.VoiceTelephoneNumber)<br>
<a style=\"color: SteelBlue\" href="mailto:$($user.EmailAddress)">$($user.EmailAddress)</a><br>
<a style=\"color: SteelBlue\" href="http://@WEBSITE">@WEBSITE</a><br>
<br><br>
</div>
</body>
</html>
"@

$txtTemplate = @"
Mit freundlichen Grüßen

$($user.Surname) $($user.GivenName)
$($user.Title)
@COMPANY
@STREETADDRESS, @POSTALCODE @CITY
TEL $($user.VoiceTelephoneNumber)
$($user.EmailAddress)
@WEBSITE
"@

# Pfad zum Signaturordner von Outlook
$signatureFolder = "$env:APPDATA\Microsoft\Signatures"

# Erstellen der HTML-Signaturdatei
$htmlSignaturePath = "$signatureFolder\HTML-Signatur.htm"
$htmlTemplate | Out-File -FilePath $htmlSignaturePath -Encoding UTF8

# Erstellen der RTF-Signaturdatei
$rtfSignaturePath = "$signatureFolder\RTF-Signatur.rtf"
$txtTemplate | Out-File -FilePath $rtfSignaturePath -Encoding UTF8

# Erstellen der TXT-Signaturdatei
$txtSignaturePath = "$signatureFolder\TXT-Signatur.txt"
$txtTemplate | Out-File -FilePath $txtSignaturePath -Encoding UTF8

# Hinzufügen der Signatur in die Registry (optional)
$regPath = "HKCU:\Software\Microsoft\Office\16.0\Common\MailSettings"
Set-ItemProperty -Path $regPath -Name "NewSignature" -Value "HTML-Signatur"
Set-ItemProperty -Path $regPath -Name "ReplySignature" -Value "HTML-Signatur"
Set-ItemProperty -Path $regPath -Name "ForwardSignature" -Value "HTML-Signatur"

Write-Host "Die E-Mail-Signaturen wurden erfolgreich erstellt und in den Outlook-Signaturordner kopiert."
